INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'101', N'Zak�ad prefabrykacji betonu WODPOL-BUD', 4, N'Kupiecka, 03-046 Warszawa, wojew�dztwo mazowieckie Polska', 7, N'Warszawa', CAST(N'2021-12-05T09:40:00.0000000' AS DateTime2), 52.3195763, 21.0103869, N'<p>Miejsce warte do zwiedzenia. Najlepiej wej\u015b\u0107 od ty\u0142u zak\u0142adu. Wej\u015bcie do \u015brodka jest dost\u0119pne przez wpi\u0119cie si\u0119 na okna w po\u0142owie zamurowene, polecam do pomocy drzewo obok. Jedynie nie mo\u017cna wej\u015b\u0107 do kot\u0142owni, biur, gdzie mo\u017ce by\u0107 ochroniarz ale na luzie z nim i do elektryki. </p>\n', NULL, NULL, NULL, NULL)
