INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'38', N'Opuszczony dom w lesie', 3, N'Kamieniec, wojew�dztwo �wi�tokrzyskie Polska', 13, N'Kamieniec', CAST(N'2022-03-16T08:13:00.0000000' AS DateTime2), 50.4619641, 21.2457119, N'<p>Opuszczony dom g\u0142\u0119bokim lesie. Nieogrodzony, drzwi otwarte, kilka szaf i masa makulatury (stare gazety, ksi\u0105\u017cki itp). Na terenie stodo\u0142a, zawalona ziemianka i dom ze strychem. </p>\n<p>Historia:<br />\nRodzina rozpad\u0142a si\u0119 z powodu silnego alkoholizmu m\u0119\u017ca. Matka rozwiod\u0142a si\u0119 i wyjecha\u0142a z dzieckiem do stan\xf3w. Facet stoczy\u0142 si\u0119 na dno, a budynek opustosza\u0142. </p>\n<p>Budynek zosta\u0142 przez kogo\u015b przeszukany, zabrali wszystko co cenne i co si\u0119 da\u0142o zez\u0142omowa\u0107.</p>\n', NULL, NULL, NULL, NULL)
