INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'401', N'Urbex Mysiad�o/Piaseczno/Warszawa.', 13, N'Pu�awska 02-882 Mysiad�o wojew�dztwo mazowieckie Polska', 7, N'Mysiad�o', CAST(N'2020-12-13T19:00:00.0000000' AS DateTime2), 52.101589075239168, 21.021861433982853, N'<p>Niestety nie znam historii tego budynku, nie wiem nawet czym dok\u0142adnie by\u0142 w czasach jego funkcjonowania.<br />\nJest w dosy\u0107 z\u0142ym stanie,  ale jest co ogl\u0105da\u0107, a gdy jest ciemno i wejdzie si\u0119 na dach,  mo\u017cna podziwia\u0107 \u0142adnie o\u015bwietlon\u0105 przez reflektory ulic\u0119 Pu\u0142awsk\u0105. W sk\u0142ad tej budowli wchodz\u0105: wielka hala, korytarz prowadz\u0105cy do wielu ma\u0142ych pomieszcze\u0144 oraz "ogr\xf3d" otoczony wysokimi murami z drutem kolczastym. W \u015brodku hali mieszka (a przynajmniej gdy to pisz\u0119) niegro\u017any bezdomny w ma\u0142ej budce, kt\xf3ry w pojebany spos\xf3b \u015bwieci\u0142 latark\u0105 w moj\u0105 stron\u0119. Jak wcze\u015bniej pisa\u0142em, jest \u0142atwa mo\u017cliwo\u015b\u0107 wej\u015bcia na dach i budynek jest bezpieczny do st\u0105pania po nim.</p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'402', N'Budynek administracyjny &#8220;Ch�odni Kielce&#8221;', 9, N'25-560 Kielce Polska', 13, N'Kielce', CAST(N'2020-12-13T19:00:00.0000000' AS DateTime2), 50.885925300216741, 20.629320144653324, N'<p>Budynek zosta\u0142 zamkni\u0119ty na prze\u0142omie ostatnich lat (wnioskuj\u0105c po kalendarzu wewnatrz obiektu by\u0142 to najprawdopodobniej 2017). W \u015brodku mo\u017cemy spotka\u0107 par\u0119 fant\xf3w, co lekko podnosi jego ocen\u0119. Na dworze za\u015b poza zaro\u015bni\u0119tym terenem jedynie spotkamy gruz.<br />\nObiekt najprawdopodobniej nie jest strze\u017cony. S\u0105siaduje z nim budynek mieszkalny, kt\xf3ry za pierwszym razem lekko nas zaniepokoi\u0142, za\u015b na przeciwko cmentarz Piaski. Wej\u015bcie bezproblemowe - widoczna dziura w drzwiach. </p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'403', N'Opuszczone miejsce do sprawiania ryb i w�dzarnia', 6, N'Rybak�wka 05-500 �abieniec wojew�dztwo mazowieckie Polska', 7, N'�abieniec', CAST(N'2020-12-13T18:59:00.0000000' AS DateTime2), 52.051920441042149, 21.028544423606949, N'<p>Budynek ze sporym "basenem" i otwartymi beczkami z sol\u0105, obok niego r\xf3wnie\u017c znajduje si\u0119 ma\u0142a w\u0119dzarnia z do\u015b\u0107 wysokim kominem.</p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'404', N'Opuszczona fabryka w stanie surowym', 13, N'Przemys�owa 15D89-333 Osiek nad Noteci� Praczwojew�dztwo wielkopolskie Polska', 15, N'Osiek nad Noteci�', CAST(N'2020-12-13T18:59:00.0000000' AS DateTime2), 53.1171986639275, 17.298016548156742, N'<p>Opuszczona fabryka, nie znalaz\u0142em o niej za du\u017co informacji niestety. Oczywi\u015bcie jest to teren prywatny i grozi zawaleniem, brak stra\u017cnik\xf3w, ewentualnie mo\u017ce was zobaczy\u0107 policja (obiekt jest przy ulicy) lub mieszka\u0144cy (obok s\u0105 domy mieszkalne). Zwiedza\u0107 ze szczeg\xf3ln\u0105 ostro\u017cno\u015bci\u0105. Mo\u017cna tam zobaczy\u0107 silosy, du\u017ce hale, pozosta\u0142o\u015bci. Sprz\u0119t\xf3w niestety nie ma. Nie zrobi\u0142em \u017cadnych zdj\u0119\u0107, gdy\u017c uzna\u0142em to zbyt na ryzykowne (zwiedzali\u015bmy po ciemku, by\u0142oby nas wida\u0107). Obiekt jest otoczony "murem" z drutem kolczastym. Da si\u0119 przez niego przej\u015b\u0107 od strony tor\xf3w i od strony jednej z polnych dr\xf3g. Obiekt jest du\u017cy i mo\u017cna zwiedzi\u0107 wiele pomieszcze\u0144.</p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'405', N'Opuszczony dw�r', 7, N'89-400 Skarpa wojew�dztwo kujawsko-pomorskie Polska', 2, N'Skarpa', CAST(N'2020-12-13T18:59:00.0000000' AS DateTime2), 53.499215287060409, 17.628829479217533, N'<p>Opuszczony dw\xf3r znajduje si\u0119 obok czynnych PGR-\xf3w, jest tak obro\u015bni\u0119ty, \u017ce nic nie wida\u0107. Nikt tego nie pilnuje. W po\u0142owie zawalony. Wchodzi si\u0119 przez las, jedno okno jest otwarte do wej\u015bcia. Ciekawy obiekt.</p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'406', N'Spalony dom przy torach', 3, N'Wi�zowa 205-500 Piaseczno wojew�dztwo mazowieckie Polska', 7, N'Piaseczno', CAST(N'2020-12-13T18:59:00.0000000' AS DateTime2), 52.060160168127879, 21.024220705949123, N'<p>Kr\u0105\u017cy plotka, \u017ce czasem mo\u017cna tam spotka\u0107 narkomana z wiatr\xf3wk\u0105 pilnuj\u0105cego tego obiektu...<br />\nAle zapewne jest to tylko mit, nie ma si\u0119 czego obawia\u0107 \U0001f600<br />\nDom jak sama nazwa m\xf3wi, jest spalony, posiada on r\xf3wnie\u017c ma\u0142\u0105 piwniczk\u0119.<br />\nDost\u0119p do tego urbexu nie jest w \u017caden spos\xf3b utrudniony \u017cadnym ogrodzeniem itd.<br />\nW okolicy cz\u0119sto przeje\u017cd\u017caj\u0105 poci\u0105gi, g\u0142\xf3wnie KM.</p>\n', NULL, NULL, NULL, NULL)
INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'407', N'Opuszczony pa�ac w Ogrodnicy', 7, N'Luboradz wojew�dztwo dolno�l�skie Polska', 1, N'Luboradz', CAST(N'2020-12-13T18:59:00.0000000' AS DateTime2), 51.14755961659408, 16.570322513580326, N'<p>Opuszczony pa\u0142ac w Ogrodnicy, obecnie w\u0142asno\u015b\u0107 prywatna. W\u0142a\u015bciciel dba o to, aby obiekt nie by\u0142 niszczony przez wandali.</p>\n<p>Link do obszernego artyku\u0142u na temat obiektu: https://nieustanne-wedrowanie.pl/wies-ogrodnica/</p>\n', NULL, NULL, NULL, NULL)
