INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'535', N'M�yn pu�tuski', 4, N'Warszawska 2306-100 Pu�tusk wojew�dztwo mazowieckie Polska', 7, N'Pu�tusk', CAST(N'2020-06-22T13:03:00.0000000' AS DateTime2), 52.692992214170566, 21.081465483039214, N'<p>Pu\u0142tuski m\u0142yn po\u0142o\u017cony przy ulicy Warszawskiej w Pu\u0142tusku, to du\u017cy obiekt bogaty w miejsca do ciekawej eksploracji. Na jego terenie znale\u017a\u0107 mo\u017cna gabinety, hale produkcyjne oraz miejsca sypialniane. W niekt\xf3rych pomieszczeniach zachowa\u0142y si\u0119 takie smaczki, jak meble, boazeria na \u015bcianach, kafelki charakterystyczne dla lat PRLu, czy dokumentacja pracy m\u0142yna.<br />\nBudynek nie jest strze\u017cony, a bardzo \u0142atwo mo\u017cna si\u0119 dosta\u0107 na jego teren na dwa sposoby: przez dziur\u0119 w siatce od strony Biedronki lub od strony ulicy Stefana Zielonki, gdzie r\xf3wnie\u017c mo\u017cna \u0142atwo przedosta\u0107 si\u0119 przez otw\xf3r w ogrodzeniu.<br />\nNie jest to obiekt strze\u017cony. Jedyne zagro\u017cenie, to bezdomni, ale ci raczej nie sprawiaj\u0105 problem\xf3w., a w ci\u0105gu dnia przewa\u017cnie opuszczaj\u0105 obiekt ;) </p>\n', NULL, NULL, NULL, NULL)
