INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'793', N'Browar Zarszyn, p�niejsze Ch�odnie Zgoda &#8211; Zarszyn', 4, N'Browarna 1338-530 Posada Zarszy�ska wojew�dztwo podkarpackie Polska', 9, N'Posada Zarszy�ska', CAST(N'2019-08-27T20:02:00.0000000' AS DateTime2), 49.577840022416581, 22.012776732444767, N'<p>Browar Zarszyn obecnie nieczynny, funkcjonowa\u0142 w latach 1885-1982. Nale\u017ca\u0142 do Zak\u0142ad\xf3w Piwowarskich w \u0141a\u0144cucie. W najlepszym latach produkcja si\u0119ga\u0142a 20tys. hektolitr\xf3w. P\xf3\u017aniej na terenie browaru otwarte zosta\u0142y ch\u0142odnie firmy Zgoda. Na miejscu s\u0105 magazyny z ch\u0142odziarkami tej firmy. Wej\u015bcie na teren znajduje si\u0119\xa0po prawej stronie od wej\u015bcia g\u0142\xf3wnego, nale\u017cy wjecha\u0107 w \u017cwirow\u0105 uliczk\u0119 po prawej, po mini\u0119ciu stawu po prawej nale\u017cy skr\u0119ci\u0107 w lewo, aby drugi staw mie\u0107 po swojej prawej i id\u0105c prosto po lewej stronie jest odchylany kawa\u0142ek siatki.</p>\n', NULL, NULL, NULL, NULL)
