INSERT [dbo].[Miejsca] ([id], [Nazwa], [Miejsce_Kategoria], [Adres], [Miejsce_wojewodztwo], [Miasto], [DataDodania], [WspolrzedneLAT], [WspolrzedneLNG], [Opis], [ZdjecieId], [Trudnosc], [Doswiadczenie], [Link]) VALUES (N'90', N'Burzowiec (kana� ulgi, kolektor przelewowy)', 8, N'Ignacego Mo�cickiego, 33-100 Tarn�w, wojew�dztwo ma�opolskie Polska', 6, N'Tarn�w', CAST(N'2022-01-03T08:26:00.0000000' AS DateTime2), 50.0185513, 20.9375234, N'<p>Nieco zapomniany kana\u0142 burzowy o d\u0142ugo\u015bci 400m. \u015arednica rury to 140cm. Wej\u015bcie zakrywa, niedomkni\u0119ta i zardzewia\u0142a na zawiasach klapa zamykaj\u0105ca dost\u0119p w razie wysokiego stanu rzeki Bia\u0142a, kt\xf3ra ma zapobiec tzw. cofce. Dnem Burzowca p\u0142ynie niewielka str\xf3\u017cka wody. Po drodze wyst\u0119puj\u0105 pomieszczenia ze studzienkami kanalizacyjnymi wyprowadzaj\u0105cymi na powierzchni\u0119. Kana\u0142 burzowy doprowadza do komory gdzie poprzecznie przebiega g\u0142\xf3wny kolektor og\xf3lnosp\u0142awny. Ma \u015brednic\u0119 od 1,6-2,0m. Nim z kolei mo\u017cna dotrze\u0107 w r\xf3\u017cne zak\u0105tki miasta - niestety tu ju\u017c nie pachnie... jak to w kanale...<br />\nUWAGA!!! W razie spodziewanego deszczu nie wchodzi\u0107!!! Fajnie je\u017adzi si\u0119 tu mini rowerkiem ;-)</p>\n', NULL, NULL, NULL, NULL)
